                                tagNames: ['h2'],
                                contentDefault: '<b>H1</b>',
                                classList: ['customClassName'],
                                attrs: {
                                    'data-custom-attr': 'custom-value'
                                }
                            },
                            {
                                name: 'h2',
                                getAction: function () {
                                    return 'append-h3';
                                },
                                getAria: function () {
                                    return 'fake h2';
                                },
                                getTagNames: function () {
                                    return ['h3'];
                                },
                                contentDefault: '<b>H2</b>'
                            }
                        ]
                    }
                }),
