            expect(buttonTwo.getAttribute('title')).toBe('fake h2');
            expect(buttonTwo.innerHTML).toBe('<b>H2</b>');

            selectElementContentsAndFire(editor.elements[0].querySelector('h3'), { eventToFire: 'mouseup' });
